    ASSERT_EQ(sjob->height_, 139726);
    ASSERT_EQ(sjob->txCount_, 9);
    ASSERT_EQ(sjob->minTime_, 1498645428u);
    ASSERT_EQ(sjob->maxTime_, sjob->header_.GetBlockTime() + 2*60*60 - 60);
